import React from 'react';
import { cx } from 'emotion';
import {
  Flex,
  type CommonProps,
  type PolymorphicProps,
  type PolymorphicComponent,
  type ExpandProps,
} from '@contentful/f36-core';

import { styles } from './TextLink.styles';
import { TextLinkVariant } from './types';

const TEXT_LINK_DEFAULT_TAG = 'a';

interface TextLinkInternalProps extends CommonProps {
  children?: React.ReactNode;
  /**
   * Determines style variation of TextLink component
   * @default primary
   */
  variant?: TextLinkVariant;
  /**
   * Disabled interaction and applies disabled styles
   * @default false
   */
  isDisabled?: boolean;
  /**
   * Expects any of the icon components
   */
  icon?: React.ReactElement;
  /**
   * Determines the icon position regarding the link text
   * @default start
   */
  alignIcon?: 'start' | 'end';
  /**
   * The element used for the root node.
   * @default a
   */
  as?: 'a' | 'button';
}

export type TextLinkProps<
  E extends React.ElementType = typeof TEXT_LINK_DEFAULT_TAG,
> = PolymorphicProps<TextLinkInternalProps, E, 'disabled'>;

function _TextLink<E extends React.ElementType = typeof TEXT_LINK_DEFAULT_TAG>(
  props: TextLinkProps<E>,
  ref: React.Ref<any>,
) {
  const {
    children,
    className,
    testId = 'cf-ui-text-link',
    variant = 'primary',
    href,
    icon,
    alignIcon = 'start',
    isDisabled,
    as = TEXT_LINK_DEFAULT_TAG,
    ...otherProps
  } = props;

  const commonProps = {
    ref,
    className: cx(
      styles.textLink({
        variant,
        isDisabled,
      }),
      props.className,
    ),
    ['data-test-id']: testId,
    ...otherProps,
  };

  const iconContent = icon ? (
    <Flex as="span">
      {React.cloneElement(icon, {
        className: cx(icon.props.className, styles.textLinkIcon()),
        size: 'small',
      })}
    </Flex>
  ) : null;

  const commonContent = (
    <>
      {icon && alignIcon === 'start' && iconContent}
      {children && (
        <span
          className={styles.textLinkText({
            alignIcon: icon ? alignIcon : undefined,
          })}
        >
          {children}
        </span>
      )}
      {icon && alignIcon === 'end' && iconContent}
    </>
  );

  if (as === 'button') {
    return (
      <button {...commonProps} disabled={isDisabled} type="button">
        {commonContent}
      </button>
    );
  }

  return (
    <a
      {...commonProps}
      onClick={
        isDisabled
          ? (e) => {
              e.preventDefault();
            }
          : commonProps.onClick
      }
      href={href}
      {...(isDisabled ? { tabIndex: -1 } : {})}
    >
      {commonContent}
    </a>
  );
}

_TextLink.displayName = 'TextLink';

export const TextLink: PolymorphicComponent<
  ExpandProps<TextLinkInternalProps>,
  typeof TEXT_LINK_DEFAULT_TAG,
  'disabled'
> = React.forwardRef(_TextLink);
import { css } from 'emotion';
import tokens from '@contentful/f36-tokens';
import { TextLinkProps } from './TextLink';
import { TextLinkVariant } from './types';

const variantToStyles = (variant: TextLinkVariant) => {
  switch (variant) {
    case 'primary':
      return {
        color: tokens.blue600,
        '&:hover, &:focus': {
          color: tokens.blue700,
        },
      };
    case 'secondary':
      return {
        color: tokens.gray600,
        '&:hover, &:focus': {
          color: tokens.gray700,
        },
      };
    case 'positive':
      return {
        color: tokens.green600,
        '&:hover, &:focus': {
          color: tokens.green700,
        },
      };
    case 'negative':
      return {
        color: tokens.red600,
        '&:hover, &:focus': {
          color: tokens.red700,
        },
      };
    case 'muted':
      return {
        color: tokens.gray400,
        '&:hover, &:focus': {
          color: tokens.gray500,
        },
      };
    case 'white':
      return {
        color: tokens.colorWhite,
        '&:hover, &:focus': {
          color: tokens.gray100,
        },
      };
    case 'premium':
      return {
        color: tokens.purple600,
        '&:hover, &:focus': {
          color: tokens.purple700,
        },
      };
    default:
      return { color: tokens.colorWhite };
  }
};

const textLink = ({
  variant,
  isDisabled,
}: Pick<TextLinkProps, 'variant' | 'isDisabled'>) =>
  css({
    display: 'inline-flex',
    justifyContent: 'center',
    alignItems: 'center',
    boxSizing: 'border-box',
    border: 0,
    padding: 0,
    margin: 0, // remove the default button margin in Safari.
    fontFamily: tokens.fontStackPrimary,
    fontSize: tokens.fontSizeM,
    fontWeight: tokens.fontWeightMedium,
    transition: `color ${tokens.transitionDurationShort} ${tokens.transitionEasingDefault}`,
    textDecoration: 'none',
    background: 'none',
    appearance: 'none',
    whiteSpace: 'normal',
    textAlign: 'left',
    cursor: isDisabled ? 'not-allowed' : 'pointer',
    opacity: isDisabled ? 0.5 : 1,
    ...variantToStyles(variant),
    outline: 'none',
    verticalAlign: 'bottom',
    '&:focus, &:focus-visible, &:hover': {
      textDecoration: isDisabled ? 'none' : 'underline',
    },
    '&:focus': {
      boxShadow: isDisabled ? 'none' : tokens.glowPrimary,
      borderRadius: tokens.borderRadiusSmall,
    },
    '&:focus:not(:focus-visible)': {
      borderRadius: 0,
      boxShadow: 'none',
    },
    '&:focus-visible': {
      borderRadius: tokens.borderRadiusSmall,
      boxShadow: isDisabled ? 'none' : tokens.glowPrimary,
    },
  });

const textLinkIcon = () =>
  css({
    fill: 'currentColor',
    transition: `fill ${tokens.transitionDurationShort} ${tokens.transitionEasingDefault}`,
  });

const textLinkText = ({ alignIcon }: Pick<TextLinkProps, 'alignIcon'>) => {
  if (alignIcon === 'start') {
    return css({
      marginLeft: tokens.spacing2Xs,
    });
  }

  if (alignIcon === 'end') {
    return css({
      marginRight: tokens.spacing2Xs,
    });
  }
};

export const styles = {
  textLink,
  textLinkIcon,
  textLinkText,
};
